mapper.select:

	SELECT *
	FROM 
		 S61.T6110			

			
	ENABLE_FLAG=1			

		AND F01=#{f01}


		AND UPPER(F02) like UPPER(CONCAT(CONCAT('%', #{f02}), '%'))


		AND UPPER(F03) like UPPER(CONCAT(CONCAT('%', #{f03}), '%'))


		AND UPPER(F04) like UPPER(CONCAT(CONCAT('%', #{f04}), '%'))


		AND UPPER(F05) like UPPER(CONCAT(CONCAT('%', #{f05}), '%'))


		AND F06=#{f06}


		AND F07=#{f07}


		AND F08=#{f08}


		AND F09=#{f09}


		AND F10=#{f10}


		AND F11=#{f11}


		AND F12=#{f12}


		AND F13=#{f13}


	ORDER BY F01


	SELECT 
		*
	FROM 
		 S61.T6110			

			
	ENABLE_FLAG=1			

		AND F01=#{bo.f01}


		AND UPPER(F02) like UPPER(CONCAT(CONCAT('%', #{bo.f02}), '%'))


		AND UPPER(F03) like UPPER(CONCAT(CONCAT('%', #{bo.f03}), '%'))


		AND UPPER(F04) like UPPER(CONCAT(CONCAT('%', #{bo.f04}), '%'))


		AND UPPER(F05) like UPPER(CONCAT(CONCAT('%', #{bo.f05}), '%'))


		AND F06=#{bo.f06}


		AND F07=#{bo.f07}


		AND F08=#{bo.f08}


		AND F09=#{bo.f09}


		AND F10=#{bo.f10}


		AND F11=#{bo.f11}


		AND F12=#{bo.f12}


		AND F13=#{bo.f13}


	ORDER BY ${page.sortFieldName} DESC
